 use std::collections::HashMap;
 use itertools::Itertools;

fn print_hash_map(map: &HashMap<usize, TreeNode>) {
    // for (key, value) in &*map {
    for key in map.keys().sorted() {
        // println!("{:3} / {:?}", &key, &value);
        println!("{:3} / {:?}", &key, map[key]);
    }
    // map.clear();
}

 #[derive(Debug)]
struct TreeNode {
    pos: usize,
    parent: usize,
    children: Vec<usize>,
    depth: u32
}

fn create_node(w: usize, parent: usize, tree: &mut HashMap<usize, TreeNode>) {
    println!("{}, child of {}", w, &parent);
    let p = tree.get_mut(&parent).unwrap();
    // TODO: mettere questo nuovo nodo sul parent
    p.children.push(w);
    let n = TreeNode {
        pos: w,
        parent: parent,
        children: Vec::new(),
        depth: p.depth + 1
    };
    tree.insert(n.pos, n);
}

